INSERT INTO public.crew_app_shipcomponent (id, name, description, traits, cost) VALUES (2, 'Medical Bay', 'Fully stocked medical facility to aid long-term recovery. Each campaign turn when recovering from Injuries, select a crew member who can mark off 2 campaign turns of recovery.', null, 25);
INSERT INTO public.crew_app_shipcomponent (id, name, description, traits, cost) VALUES (3, 'Cargo Hold', 'The hold of the ship has been upgraded to be environmentally stable. When traveling to a new planet, you may take on cargo. Roll 2D6 and discard any 5-6. Select the highest remaining die and ear that many credits from delivering a shipment to the new world. If both dice are discarded, no shipments are available. If your ship is damaged in transit, the cargo is also lost.', null, 15);
INSERT INTO public.crew_app_shipcomponent (id, name, description, traits, cost) VALUES (4, 'Database', 'Extensive data records have been added to aid in decision making. When traveling to a new planet, you may roll up the details for one additional planet, and then select which to visit.', null, 10);
INSERT INTO public.crew_app_shipcomponent (id, name, description, traits, cost) VALUES (5, 'Shuttle', 'Launch bay with a standard "Lemon Shark" shuttle for quick deployments. If you receive the Distress Call Starship Travel event, you may roll twice and pick the higher roll. If a planet is Invaded, you may add +2 to the roll to get off-world.', null, 15);
